package June7;

public class StringtoArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String s1=" my name is vijayakumar" ;
		
		char[] s2 = s1.toCharArray();
		
		for(int i= 0 ; i<s2.length ; i++)
		{
			
			System.out.println(s2[i]);
		}
	}

}
